package Template::Grammar;

use strict;
use warnings;

our $VERSION  = '3.100';

my (@RESERVED, %CMPOP, $LEXTABLE, $RULES, $STATES);
my ($factory, $rawstart);


#========================================================================

# Reserved words, comparison and binary operators
#========================================================================

BEGIN {
@RESERVED = qw(
	GET CALL SET DEFAULT INSERT INCLUDE PROCESS WRAPPER BLOCK END
	USE PLUGIN FILTER MACRO PERL RAWPERL TO STEP AND OR NOT DIV MOD
	IF UNLESS ELSE ELSIF FOR NEXT WHILE SWITCH CASE META IN
	TRY THROW CATCH FINAL LAST RETURN STOP CLEAR VIEW DEBUG
    );

# for historical reasons, != and == are converted to ne and eq to perform
# stringwise comparison (mainly because it doesn't generate "non-numerical
# comparison" warnings which != and == can) but the others (e.g. < > <= >=)
# are not converted to their stringwise equivalents.  I added 'gt' et al,
# briefly for v2.04d and then took them out again in 2.04e.


%CMPOP = qw(
    != ne
    == eq
    <  <
    >  >
    >= >=
    <= <=
);

#    eq eq  # add these lines to the above to
#    lt lt  # enable the eq, lt and gt operators
#    gt gt

#========================================================================
# Lexer Token Table
#========================================================================

# lookup table used by lexer is initialised with special-cases
$LEXTABLE = {
    'FOREACH' => 'FOR',
    'BREAK'   => 'LAST',
    '&&'      => 'AND',
    '||'      => 'OR',
    '!'       => 'NOT',
    '|'	      => 'FILTER',
    '.'       => 'DOT',
    '_'       => 'CAT',
    '..'      => 'TO',
#    ':'       => 'MACRO',
    '='       => 'ASSIGN',
    '=>'      => 'ASSIGN',
#    '->'      => 'ARROW',
    ','       => 'COMMA',
    '\\'      => 'REF',
    'and'     => 'AND',		# explicitly specified so that qw( and or
    'or'      => 'OR',		# not ) can always be used in lower case,
    'not'     => 'NOT',		# regardless of ANYCASE flag
    'mod'     => 'MOD',
    'div'     => 'DIV',
};

# localise the temporary variables needed to complete lexer table
{
    my @tokens = qw< ( ) [ ] { } ${ $ + / ; : ? >;
    my @cmpop  = keys %CMPOP;
    my @binop  = qw( - * % );              # '+' and '/' above, in @tokens

    # fill lexer table, slice by slice, with reserved words and operators
    @$LEXTABLE{ @RESERVED, @cmpop, @binop, @tokens }
			= ( @RESERVED, ('CMPOP') x @cmpop, ('BINOP') x @binop, @tokens );
}

} # --- END BEGIN

#========================================================================
# CLASS METHODS
#========================================================================

sub new {
    my $class = shift;
    bless {
		LEXTABLE => $LEXTABLE,
		STATES   => $STATES,
		RULES    => $RULES,
    }, $class;
}

# track usages of objects using the factory
#	this is an over complex object
#	used to track how many objects are currently using the shared factory
my $_factory_usages;

DESTROY {
	my ( $self ) = @_;

	# on Grammar destruction check if we can safely trigger the destroy for the factory
	$self->unregister_factory() if $self;

	return;
}

sub unregister_factory {
	my ( $self ) = @_;

	return unless $self && ref $_factory_usages;
	return unless "$factory" eq $_factory_usages->{CURRENT};

	if ( $_factory_usages->{HOLD_BY}->{ "$self" } ) {
		delete $_factory_usages->{HOLD_BY}->{ "$self" };
	}

	if ( ! scalar keys %{ $_factory_usages->{HOLD_BY} } ) {
		# avoid a memory leak from factory
		undef $factory;
		undef $_factory_usages;
	}

	return;
}

sub register_factory {
	my ( $self ) = @_;

	return unless $factory;

	$_factory_usages //= { CURRENT => "", HOLD_BY => {} };

	if ( "$factory" ne $_factory_usages->{CURRENT} ) {
		# we have updated the factory, should not care about the previous one...
		$_factory_usages->{HOLD_BY} = {}; 		  # reset who hold the factory
		$_factory_usages->{CURRENT} = "$factory"; # stringify it
	}

	$_factory_usages->{HOLD_BY}->{ "$self" } = 1; # we are using this factory

	return;
}

# update method to set package-scoped $factory lexical
sub install_factory {
    my ($self, $new_factory) = @_;

    $factory = $new_factory;

    # register the current factory in order to clean it on destroy if possible
    $self->register_factory();

    return $factory;
}


